import React, { Component } from "react";
import './search.css';
class Search extends Component {
    state = {
        searchValue: '',
        category: '',
        outputs: []
    };
    render() {
        return (
            <div>
            <h1>Search for starships, films or vehicles from the Star Wars universe!</h1>
            <h2>Please specify a category below.</h2>
            <button value="films" onClick={this.categoryChange}>Films</button>
            <button value="starships" onClick={this.categoryChange}>Starships</button>
            <button value="vehicles" onClick={this.categoryChange}>Vehicles</button>
            <input
                name="text"
                type="text"
                placeholder="Search"
                onChange={event => this.searchChange(event)}
                value={this.state.searchValue}
            />
            <button onClick={this.handleSearch}>Search</button>
            {this.state.outputs ? (
                <div>
                    {this.state.outputs.map((output, index) => (
                        <div key={index}>
                            <h1>{output.strOutput}</h1>
                            <img src={output.strOutputThumb} alt="output-thumbnail" />
                         </div>
                    ))}
                </div>
                ) : (
                <p>Try searching for a starship, film or vehicle</p>
            )}
            </div>
        );
    }
    categoryChange = event => {
        this.setState({ category: event.target.value });
        this.makeApiCall('', this.state.category);
    };
    searchChange = event => {
        this.setState({ searchValue: event.target.value });
    };
    handleSearch = () => {
        this.makeApiCall(this.state.searchValue, '');
    }
    makeApiCall = (searchInput, contentType) => {
        var searchUrl = `https://swapi.dev/api/${contentType}?search=${searchInput}`;
        fetch(searchUrl).then(response => {
            return response.json();
        }).then(jsonData => {
            this.setState({ outputs: jsonData.outputs });
            console.log(jsonData.outputs);
        });
    };
}
export default Search;
