import './App.css';
import '../src/fonts/Starjhol.ttf'
import logo from '../src/assets/star-wars-logo.png';
import Search from './starWarsComponents/js/search';
import Category from './starWarsComponents/js/category';
import React, { Component, useState } from 'react';

function App() {
  const [state, setState] = useState({
    searchValue: "",
    category: "",
    outputs: []
  });

  return (
    <div>
    <img className="logo" src={logo}/>
    <h1>Search for starships, films or vehicles from the Star Wars universe!</h1>
    <h2>Please specify a category below.</h2>

    <div className="searchBoxContainer">
      <Category categoryChange={categoryChange} state={state} />
      <Search handleSearch={handleSearch} searchChange={searchChange} state={state} />
    </div>

    {this.state.outputs ? (
        <div>
            {this.state.outputs.map((output, index) => (
                <div key={index}>
                {this.state.category == "films" ? (
                    <div><h3>{output.title}</h3><p>{output.director}</p></div>
                    ):(
                    <div><h3>{output.name}</h3></div>
                    )}
                 </div>
            ))}
        </div>
        ) : (
        <p>Do you have the correct category selected?</p>
    )}
    </div>
  );

}

const categoryChange = event => {
  this.setState({ category: event.target.value }, () => {
      this.inputElement.click()
  })
};
const searchChange = event => {
  this.setState({ searchValue: event.target.value });
};
const handleSearch = (e) => {
  e.preventDefault()
  makeApiCall(this.state.searchValue, this.state.category);
};
const makeApiCall = (searchInput, categoryInput) => {
  var searchUrl = `https://swapi.dev/api/${categoryInput}?search=${searchInput}`;
  fetch(searchUrl).then(response => {
      return response.json();
  }).then(jsonData => {
      this.setState({ outputs: jsonData.results });
      console.log(jsonData.results);
  });
};

export default App;
