import React, { Component, useState } from "react";
import '../css/category.css';

const Category = (props) => {
        return (
                <div className="categoryBox">
                    <button 
                        className={`category-cta ${props.state.category === "films" ? "category-active" : ""}`} 
                        value="films" >
                            Films
                        </button>
                    <button     
                        className={`category-cta ${props.state.category === "starships" ? "category-active" : ""}`} 
                        value="starships" onClick={props.categoryChange}>
                            Starships
                        </button>
                    <button 
                        className={`category-cta ${props.state.category === "vehicles" ? "category-active" : ""}`} 
                        value="vehicles" onClick={props.categoryChange}>
                            Vehicles
                        </button>
                </div>
        );
}
export default Category;
