import * as React from "react";
import { useState, useEffect, useRef } from "react";
import "../css/CategorySelect.css";

export default function CategorySelect({categoryChange, category}) {
  return (
    <div className="categoryBox">
      <button
        className={`category-cta ${
          category === "films" ? "category-active" : ""
        }`}
        value="films"
        onClick={categoryChange}
      >
        Films
      </button>
      <button
        className={`category-cta ${
          category === "starships" ? "category-active" : ""
        }`}
        value="starships"
        onClick={categoryChange}
      >
        Starships
      </button>
      <button
        className={`category-cta ${
          category === "vehicles" ? "category-active" : ""
        }`}
        value="vehicles"
        onClick={categoryChange}
      >
        Vehicles
      </button>
    </div>
  );
}
