import React, { Component } from "react";
import './search.css';
import './category.css';
import './fonts/Starjhol.ttf'
import logo from './assets/star-wars-logo.png';
class Search extends Component {
    state = {
        searchValue: '',
        category: '',
        outputs: []
    };
    render() {
        return (
            <div>
            <img className="logo" src={logo}/>
            <h1>Search for starships, films or vehicles from the Star Wars universe!</h1>
            <h2>Please specify a category below.</h2>
            <button className={`category-cta glow-on-hover ${this.state.category === "films" ? "category-active" : ""}`} value="films" onClick={this.categoryChange}>Films</button>
            <button className={`category-cta glow-on-hover ${this.state.category === "starships" ? "category-active" : ""}`} value="starships" onClick={this.categoryChange}>Starships</button>
            <button className={`category-cta glow-on-hover ${this.state.category === "vehicles" ? "category-active" : ""}`} value="vehicles" onClick={this.categoryChange}>Vehicles</button>
            <input
                name="text"
                type="text"
                placeholder="Search"
                onChange={event => this.searchChange(event)}
                value={this.state.searchValue}
            />
            <button className="search-cta" onClick={this.handleSearch}>Search</button>
            {this.state.outputs ? (
                <div>
                    {this.state.outputs.map((output, index) => (
                        <div key={index}>
                        {this.state.category == "films" ? (
                            <div><h1>{output.title}</h1><p>{output.director}</p></div>
                            ):(
                            <div><h1>{output.name}</h1></div>
                            )}
                         </div>
                    ))}
                </div>
                ) : (
                <p>Do you have the correct category selected?</p>
            )}
            </div>
        );
    }
    categoryChange = event => {
        this.setState({ category: event.target.value }, () => {
            this.handleSearch()
        })
    };
    searchChange = event => {
        this.setState({ searchValue: event.target.value });
    };
    handleSearch = () => {
        this.makeApiCall(this.state.searchValue, this.state.category);
    }
    makeApiCall = (searchInput, categoryInput) => {
        var searchUrl = `https://swapi.dev/api/${categoryInput}?search=${searchInput}`;
        fetch(searchUrl).then(response => {
            return response.json();
        }).then(jsonData => {
            this.setState({ outputs: jsonData.results });
            console.log(jsonData.results);
        });
    };
}
export default Search;
