import React, { Component, useState } from "react";
import '../css/search.css';
import '../css/category.css';

const Search = (props) => {
        function categoryChange (event) {
            props.setState({ ...props.state, category: event.target.value });
            handleSearch();
        };
        function searchChange (event) {
            props.setState({ ...props.state, searchValue: event.target.value });
        };
        function handleSearch () {
            makeApiCall( props.state.searchValue,  props.state.category);
        };
        function makeApiCall (searchInput, categoryInput) {
            var searchUrl = `https://swapi.dev/api/${categoryInput}?search=${searchInput}`;
            fetch(searchUrl).then(response => {
                return response.json();
            }).then(jsonData => {
                props.setState({ ...props.state, outputs: jsonData.results });
                console.log(jsonData.results);
            });
        };
        return (
            <div className="searchBoxContainer">
                <div className="categoryBox">
                <button 
                    className={`category-cta ${ props.state.category === "films" ? "category-active" : ""}`} 
                    value="films" onClick={event => categoryChange(event)}>
                        Films
                    </button>
                <button     
                    className={`category-cta ${ props.state.category === "starships" ? "category-active" : ""}`} 
                    value="starships" onClick={event => categoryChange(event)}>
                        Starships
                    </button>
                <button 
                    className={`category-cta ${ props.state.category === "vehicles" ? "category-active" : ""}`} 
                    value="vehicles" onClick={event => categoryChange(event)}>
                        Vehicles
                    </button>
                </div>
                <div className="wrap">
                    <form className="search" onSubmit={handleSearch}>
                        <input  
                            className="searchTerm"
                            name="text"
                            type="text"
                            placeholder="Search"
                            onChange={event => searchChange(event)}
                            value={props.state.searchValue}
                        />
                        <button type="submit" className="search-cta" onClick={handleSearch} >
                            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="100" height="100" viewBox="0 0 50 50" className="seach-icon">
                                <path d="M 21 3 C 11.621094 3 4 10.621094 4 20 C 4 29.378906 11.621094 37 21 37 C 24.710938 37 28.140625 35.804688 30.9375 33.78125 L 44.09375 46.90625 L 46.90625 44.09375 L 33.90625 31.0625 C 36.460938 28.085938 38 24.222656 38 20 C 38 10.621094 30.378906 3 21 3 Z M 21 5 C 29.296875 5 36 11.703125 36 20 C 36 28.296875 29.296875 35 21 35 C 12.703125 35 6 28.296875 6 20 C 6 11.703125 12.703125 5 21 5 Z"></path>
                            </svg>
                        </button>
                    </form>
                </div>
            </div>
        );
}
export default Search;
