import * as React from "react";
import { useState, useEffect } from "react";
import Header from "./starWarsComponents/js/Header";
import CategorySelect from "./starWarsComponents/js/CategorySelect";
import SearchBar from "./starWarsComponents/js/SearchBar";
import SearchResults from "./starWarsComponents/js/SearchResults";
import SortOutput from "./starWarsComponents/js/SortOutput";

export default function App() {
  const [searchValue, setSearchValue] = useState("");
  const [category, setCategory] = useState("");
  const [azActive, setAzActive] = useState(false);
  const [outputs, setOutputs] = useState([]);
  const [loading, setLoading] = useState(false);

  function sortOutput() {
    let strAscending = "";
    if (category === "films") {
      strAscending = [...outputs].sort((a, b) => (a.title > b.title ? 1 : -1));
    } else {
      strAscending = [...outputs].sort((a, b) => (a.name > b.name ? 1 : -1));
    }
    setOutputs(strAscending);
    setAzActive(true);
  }

  function categoryChange(event) {
    const value = event.target.value;
    setCategory(value);
    makeApiCall(searchValue, category);
  }

  function searchChange(event) {
    const value = event.target.value;
    setSearchValue(value);
  }

  function handleSearch(event) {
    event.preventDefault();
    makeApiCall(searchValue, category);
  }

  function makeApiCall(searchInput, categoryInput) {
    const searchUrl = `https://swapi.dev/api/${categoryInput}?search=${searchInput}`;

    setLoading(true);

    fetch(searchUrl)
      .then((response) => {
        return response.json();
      })
      .then((jsonData) => {
        console.log(jsonData.results);
        setOutputs(jsonData.results);
        setLoading(false);
        setAzActive(false);
      });
  }

  return (
    <main className="starWarsWrapper">
      <Header />
      <div className="searchBoxContainer">
        <CategorySelect onClick={() => categoryChange()} category={category} />
        <SearchBar
          onClick={handleSearch}
          onChange={searchChange}
          onSubmit={handleSearch}
          searchValue={searchValue}
        />
      </div>
      <SortOutput
        category={category}
        azActive={azActive}
        sortOutput={sortOutput}
      />
      {loading ? (
        "Loading..."
      ) : (
        <SearchResults outputs={outputs} category={category} />
      )}
    </main>
  );
}
